package com.example.gspro.com.example.object;import android.graphics.Bitmap;/** * Created by takenogaku on 15/06/11. */public class CustomData {    private Bitmap imageData_;    private String nameData_;    private String addressData_;    private int currentLoop;    public void setImagaData(Bitmap image) {        imageData_ = image;    }    public Bitmap getImageData() {        return imageData_;    }    public void setNameString(String text) {        nameData_ = text;    }    public void setAddressString(String text) {        addressData_ = text;    }    public String getNameData() {        return nameData_;    }    public String getAddressData() {        return addressData_;    }    public void setCurrentLoop(int i) {        currentLoop = i;    }    public int getCurrentLoop() {        return currentLoop;    }}